class Todo extends React.Component{
  constructor(props){
    super(props);
    this.state={item:props.item};

  }
  render(){
    return (
      <div>
        <input
            type='checkbox'
            id={this.state.item.id}
            name={this.state.item.id}
            checked={this.state.item.done}
        />        
        <label id={this.state.item.id}>{this.state.item.title}</label>
      </div>
    );
  }
}
export default Todo;
